
public class AreaOfCircle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float Radius = 1.2f;
		float pie = 3.14f;
		double Area = pie*Radius*Radius;
		System.out.println("Area of a circlr is: "+ String.format("%.2f", Area)); // will display up to 2 decimal

	}

}
