
public class ArthematicOps {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a=50, b=50;
		int c=a+b;
		int d=a*b;
		int e=a-b;
		int f=a/b;
		
		System.out.println("Sum of a and b is:" +c);
		System.out.println("Product of a and b is:" +d);
		System.out.println("difference of a and b is:" +e);
		System.out.println("div of a and b is:" +f);
		
		
	}	
	

}
